import ReactQuill from "react-quill";

function Main({activeNote, onUpdateNote, onDeleteNote}) {
    const onEditField = (key, value) => {
        onUpdateNote({
            ...activeNote,
            [key]: value,
            lastModified: Date.now(),
        });
    };

    if(!activeNote) return <div class = "no-active-note">No note selected</div>




    /*
    return (

        <div class = "app-main">
            <div class = "app-main-note-edit">
                <input
                    type = "text"
                    id = "title"
                    value = {activeNote.title}
                    onChange = {(e) => onEditField("title", e.target.value)}
                    autoFocus>
                </input>
                <textarea
                    id = "body"
                    placeholder = "Write your note here..."
                    value = {activeNote.body}
                    onChange = {(e) => onEditField("body", e.target.value)}>
                </textarea>
            </div>
            <div class = "app-main-note-preview">
                <h1 class = "preview-title">{activeNote.title}</h1>
                <div class = "markdown-preview">{activeNote.body}</div>
            </div>
        </div>
    );*/


    return (
        <div class = "app-main">
            <div class = "app-main-note-edit">
                <header class = "title-header">
                    <input
                        type = "text"
                        id = "title"
                        value = {activeNote.title}
                        onChange = {(e) => onEditField("title", e.target.value)}
                        autoFocus>
                    </input>

                    <button class = "header-delete-button" onClick = {() => onDeleteNote(activeNote.id)}>Delete</button>

                    <ReactQuill
                    class = "quill"
                    id = "body"
                    placeholder = "Write your note here..."
                    value = {activeNote.body}
                    onChange = {(e) => onEditField("body", e.target.value)}>
                    </ReactQuill>
                </header>



            </div>
            <div class = "app-main-note-preview">
                <h1 class = "preview-title">{activeNote.title}</h1>
                <div class = "markdown-preview">{activeNote.body}</div>
            </div>
        </div>
    );


}

export default Main;
