import {useEffect, useState} from "react";
import Sidebar from "./Sidebar";
import Main from "./Main";
import uuid from "react-uuid";
import { useNavigate, useParams } from "react-router-dom";

function App() {

  const data = JSON.parse(localStorage.getItem('notesStorage'));
  const [notes, setNotes] = useState(data || []);
  useEffect(() => {
    localStorage.setItem('notesStorage', JSON.stringify(notes));
  }, [notes])



  const [activeNote, setActiveNote] = useState(false);

  const [showSidebar, setShowSidebar] = useState(true);

  const navigate = useNavigate();



  const onAddNote = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled Note",
      body: "",
      lastModified: Date.now(),
    };
    setNotes([newNote, ...notes]);
    navigate(`/notes/${newNote.id}`)
  };

  const onUpdateNote = (updatedNote) => {
    const updatedNotesArray = notes.map((note) => {
      if (note.id === activeNote) {
        return updatedNote;
      }

      return note;

    });

    setNotes(updatedNotesArray);

  }

  useEffect(()=>{
    localStorage.setItem("notes", JSON.stringifynotes)
  },[])

  const onDeleteNote = (idToDelete) => {
    const ans = window.confirm("?");
    if(ans) {
      setNotes((notes) => {

        navigate(notes.filter((note) => note.id !== idToDelete).length ? `/notes/${notes.filter((note) => note.id !== idToDelete)[0].id}` : "/notes");
        notes.filter((note) => note.id !== idToDelete);
        console.log(notes)
        localStorage.setItem('notesStorage', JSON.stringify(notes));
      })
    }
  }

  const getActiveNote = () => {
    return notes.find((note) => note.id === activeNote);
  }
/*
  const showSidebar = () => {
    const sidebar = document.querySelector('.Sidebar');
    sidebar.style.display = (sidebar.style.display === 'none') ? 'block' : 'none';
  }*/

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  }


/*
  const onSaveNote = (idToSave) => {

  }*/

  return (
    <div> 
      <header>
        <div class = "items">
          <button type="button" id="info" class = "buttons" onClick = {toggleSidebar} >
            <div class = "lines"></div>
            <div class = "lines"></div>
            <div class = "lines"></div>
          </button>
        </div>
        <div>
          <h1 class = "center">Lotion</h1>
          <h6 class = "center">Like Notion, but worse.</h6>
        </div>
        <div class = "items"></div>
      </header>
      <div class = "mainnn">
        {showSidebar && (
          <Sidebar
            id = "side"
            notes = {notes}
            onAddNote = {onAddNote}
            onDeleteNote = {onDeleteNote}
            activeNote = {activeNote}
            setActiveNote = {setActiveNote}> 
          </Sidebar>
        )}

        <Main
          activeNote = {getActiveNote()}
          onUpdateNote = {onUpdateNote}
          onDeleteNote = {onDeleteNote}>
        </Main>
      </div>
    </div>

  );
}

export default App;



